mapper.select:
SELECT a.order_id, DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') create_time, su.user_name from t_order a LEFT JOIN sm_user su on su.user_id=a.create_user_id WHERE 1=1
and a.create_time > STR_TO_DATE(#{beginTime}, '%Y-%m-%d %H:%i:%s')
and a.create_time < STR_TO_DATE(#{endTime}, '%Y-%m-%d %H:%i:%s')
and a.type=#{type}
and a.create_user_id=#{userId}

SELECT  a.price,a.num,b.width,b.length,b.height,b.shape from t_order_detail a
LEFT JOIN  t_inventory b on a.inventory_id=b.inventory_id
where order_id=#{order_id}

select d.user_name,a.order_id,DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s') create_time,b.num,b.price,c.width,c.height,c.length,c.shape from t_order a LEFT JOIN t_order_detail b on a.order_id=b.order_id LEFT JOIN t_inventory c on b.inventory_id=c.inventory_id LEFT JOIN sm_user d on d.user_id=a.create_user_id where a.type=#{type}
and d.user_id=#{userId}
and a.create_time > STR_TO_DATE(#{beginTime}, '%Y-%m-%d %H:%i:%s')
and a.create_time < STR_TO_DATE(#{endTime}, '%Y-%m-%d %H:%i:%s')
ORDER BY create_time
